import { ArrowRightIcon } from "@heroicons/react/solid";
import React, { useEffect } from "react";
import "./Navbar.css";
import Aos from "aos";
import "aos/dist/aos.css";
import { useDisclosure } from "@chakra-ui/react";
import Sidebar from "./Sidebar";

export default function Navbar() {
  const { isOpen, onOpen, onClose } = useDisclosure();
  const btnRef = React.useRef();
  useEffect(() => {
    Aos.init({
      duration: 2000,
      useClassNames: true,
      initClassName: false,
      animatedClassName: "animated",
      once: true,
    });
  });
  return (
    <header
      className="headerTop bg-gray-800 top-0 bg-gray-800 mt-0 fixed top-0 overflow-hidden w-full z-[999]"
      data-aos="fade-down"
      data-aos-anchor-placement="top-bottom"
      data-aos-duration="700"
    >
      <div className="container mx-auto flex flex-wrap px-4 py-2 flex-col md:flex-row items-center justify-between">
        <div className="flex items-center">
          {" "}
          <a className="title-font font-medium text-white mb-3 md:mb-0">
            <a href="/#home" className="ml-3 text-[24px]">
              ğ’±ğ’¾ğ’¿ğ’¶ğ“
            </a>
          </a>
          <nav className="cool-link hidden md:block md:ml-6 md:py-4 md:pl-4 md:border-l md:border-gray-700	flex flex-wrap items-center text-base justify-center">
            <a href="/#about" className="mr-5 hover:text-white">
              About Me
            </a>
            <a href="/#Skills" className="mr-5 hover:text-white">
              Tools & Skills
            </a>
            <a href="/#projects" className="mr-6 hover:text-white">
              Projects
            </a>
            <a href="/#contact" className="mr-5 hover:text-white">
              Contact Me
            </a>
          </nav>
        </div>
        <a
          onClick={onOpen}
          href="/#contact"
          className="inline-flex items-center bg-gray-800 border-0 py-1 px-3 focus:outline-none hover:bg-gray-700 rounded text-base mt-4 md:mt-0 transition duration-300"
        >
          Hire Me
          <ArrowRightIcon className="w-4 h-4 ml-1" />
        </a>
      </div>
      <Sidebar isOpen={isOpen} onClose={onClose} />
    </header>
  );
}
