import React, { useState } from "react";
import Home from "./components/Home";
import Contact from "./components/Contact";
import Navbar from "./components/Navbar";
import Projects from "./components/Projects";
import About from "./components/About";
import Skills from "./components/Skills";
import Propskills from "./components/Propskills";
import Footer from "./components/Footer";
import { debounce } from "./utils/helper";

function App() {
  const [scrollY, setScrollY] = useState(0);

  const handleScroll = debounce((e) => {
    setScrollY(window.scrollY);

    const currentScrollPos = window.scrollY;
    // setDesktopNavType(
    //   +currentScrollPos > 2 * +window.screen.height ? "WHITE" : "WHITE"
    // );

    if (currentScrollPos > 20) {
      setDesktopNavType("WHITE");
    } else {
      setDesktopNavType("TRANSPARENT");
    }

    if (currentScrollPos > 40) {
      setMobileNavType("WHITE");
    } else {
      setMobileNavType("TRANSPARENT");
    }

    setPrevScrollPos(currentScrollPos);
  }, 10);

  useEffect(() => {
    handleScroll();

    window.addEventListener("scroll", handleScroll);

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, [prevScrollPos]);

  useEffect(() => {
    if (scrollY > 200) {
      setLoadGlobalImages(true);
      setLoadFeaturesImages(true);
    }
  }, [scrollY]);

  return (
    <div className="App text-gray-400 body-font max-w-[1440px] mx-auto h-[100vh] overflow-y-auto no-scrollbar">
      <Navbar />
      <Home />
      <About />
      <Skills />
      <Propskills />
      <Projects />
      <Contact />
      <Footer />
    </div>
  );
}

export default App;
